import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-button',
  templateUrl: './button.component.html',
  styleUrls: ['./button.component.css']
})
export class ButtonComponent {
  @Input() text: string;
  @Input() color: string;

  constructor(private text: string, private color: string) {
    this.text = text;
    this.color = color;
  }

  ngOnInit(): void {}
}
